mapper.select:
select id, category_id,name, list_pic_url, retail_price from goods where is_delete=0
AND is_hot=1
AND is_new=1
AND category_id=#{categoryId,jdbcType=INTEGER}
AND brand_id=#{brandId,jdbcType=INTEGER}
AND name like concat('%',#{keyword},'%')
order by retail_price ${order}
order by id DESC
